import { provideAnimations } from '@angular/platform-browser/animations';
// Import Angular Material theme
import '../src/styles.scss';
const preview = {
    parameters: {
        actions: { argTypesRegex: '^on[A-Z].*' },
        controls: {
            matchers: {
                color: /(background|color)$/i,
                date: /Date$/,
            },
        },
        docs: {
            inlineStories: true,
        },
    },
    decorators: [
        (story) => ({
            ...story(),
            providers: [
                ...(story().providers || []),
                provideAnimations(),
            ],
        }),
    ],
};
export default preview;
